import React, { useEffect, useRef } from "react";
import Header from "./Header";
import { Outlet, useLocation } from "react-router-dom";
import { Navigation, Search } from "./index";
import { Intro, Contact } from "../../components";
import { path } from "../../ultils/constant";

const Home = () => {
  const location = useLocation();
  const navRef = useRef();

  useEffect(() => {
    const handleScroll = (e) => {
      if (window.pageYOffset >= 134) {
        navRef.current.style.cssText = `
                position: fixed;
                top: 0;
                left: 0;
                right: 0;
                z-index: 50;
                `;
      } else {
        navRef.current.style.cssText = `
                width: 100%
                `;
      }
    };
    window.addEventListener("scroll", handleScroll);
    return () => {
      window.removeEventListener("scroll", handleScroll);
    };
  }, []);
  return (
    <div className="w-full flex gap-6 flex-col items-center h-full">
      <div className="flex w-full items-center justify-center mb-5">
        <Header />
      </div>
      <div ref={navRef} className="w-full">
        <Navigation />
      </div>
      {location.pathname !== `/${path.CONTACT}` &&
        location.pathname !== `/${path.LOGIN}` &&
        !location.pathname?.includes(path.DETAIL) &&
        !location.pathname?.includes(path.WISHLIST) && <Search />}
      <div className="w-full lg:w-5/6 flex flex-col items-start justify-start mt-3">
        <Outlet />
      </div>
      {/* <Intro /> */}
      <Contact />
    </div>
  );
};

export default Home;
